import React, { ChangeEvent, useState, FormEvent } from 'react';

export const Form: React.FC = () => {

    // input
    const [basalMetabolicRate, setBasalMetabolicRate] = useState<number>(0)

    // output
    const [wBasaMetabolicRate, setwBasaMetabolicRate,] = useState<number>(0)

    // 基礎代謝
    function handleChange(event: ChangeEvent<HTMLInputElement>) {
        setBasalMetabolicRate(parseInt(event.target.value))
    }


    // submit output test
    function submitHandler(event: FormEvent<HTMLFormElement>) {
        event.preventDefault()
        setwBasaMetabolicRate(basalMetabolicRate)
    }

    return (
        <div>
            <div className='wrapper'>
                <form className="contact-form" onSubmit={submitHandler}>

                    <input
                        type='number'
                        value={basalMetabolicRate}
                        min={1000}
                        max={2500}
                        onChange={handleChange}
                    />

                    <div>
                        <button className="btn">=</button>
                    </div>
                </form>
            </div>
            <div className="output">
                <p>{wBasaMetabolicRate}</p>
            </div>
        </div>
    );
}

export default Form;
