import React, { ChangeEvent, useState, FormEvent } from 'react';

export const Form: React.FC = () => {

    // input
    const [basalMetabolicRate, setBasalMetabolicRate] = useState<number>(0)
    const [intakeCoefficient, setIntakeCoefficient] = useState(true)

    // output
    const [wBasaMetabolicRate, setwBasaMetabolicRate,] = useState<number>(0)

    // 基礎代謝
    function handleChange01(event: ChangeEvent<HTMLInputElement>) {
        setBasalMetabolicRate(parseInt(event.target.value))
    }
    // 摂取係数(1.3-1.7) for diet 1.3
    function handleChange02(event: ChangeEvent<HTMLInputElement>) {
        setIntakeCoefficient(parseInt(event.target.value))
    }

    // submit output test
    function submitHandler(event: FormEvent<HTMLFormElement>) {
        event.preventDefault()
        setwBasaMetabolicRate(basalMetabolicRate)
    }

    return (
        <div>
            <div className='wrapper'>
                <form className="contact-form" onSubmit={submitHandler}>

                    <input
                        type='number'
                        value={basalMetabolicRate}
                        min={1000}
                        max={2500}
                        onChange={handleChange01}
                    />

                    <input
                        type='radio'
                        onChange={handleChange02}
                        checked={intakeCoefficient}
                    />
                    <span>1.3</span>
                    <input
                        type='radio'
                        onChange={handleChange02}
                        checked={!intakeCoefficient}
                    />
                    <span>1.4</span>
                    <input
                        type='radio'
                        onChange={handleChange02}
                        checked={!intakeCoefficient}
                    />
                    <span>1.5</span>
                    <input
                        type='radio'
                        onChange={handleChange02}
                        checked={!intakeCoefficient}
                    />
                    <span>1.6</span>
                    <input
                        type='radio'
                        onChange={handleChange02}
                        checked={!intakeCoefficient}
                    />
                    <span>1.7</span>
                    {/* <input
                        type='number'
                        value={intakeCoefficient}
                        step='0.1'
                        min={1.3}
                        max={1.7}
                        onChange={handleChange02}
                    /> */}
                    <div>
                        <button className="btn">=</button>
                    </div>
                </form>
            </div>
            <div className="output">
                <p>{wBasaMetabolicRate}</p>
            </div>
        </div>
    );
}

export default Form;
